import axios from "axios";
import { displayError, displaySuccess } from "../../Helper";
import { LOGIN_ERROR, SET_USER } from "./ActionType";
import { toastr } from "react-redux-toastr";

export const loginUser = (email, password, rememberMe) => {
  return (dispatch) => {
    axios
      .post("https://sigviewauth.sigmoid.io/signIn", {
        email,
        password,
        rememberMe,
      })
      .then((response) => {
        toastr.success("", "Sign Up SuccessFull");
        dispatch({ type: SET_USER, data: response.data });
        if (rememberMe) {
          localStorage.setItem("access_token", response.data.token);
        } else {
          sessionStorage.setItem("access_token", response.data.token);
        }
      })
      .catch((error) => {
        toastr.success("", "Wrong email or password");
        dispatch({
          type: LOGIN_ERROR,
          data: error.response.data.statusMessage,
        });
      });
  };
};
