import { SET_LOADING_DATE, SET_MIN_MAX_DATE } from "../Action/ActionType";

const initialState = {
  minDate: "",
  maxDate: "",
  isDateLoading: false,
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_MIN_MAX_DATE:
      return {
        ...state,
        minDate: action.value.startDate,
        maxDate: action.value.endDate,
      };
    case SET_LOADING_DATE: {
      return {
        ...state,
        isDateLoading: true,
      };
    }
    default:
      return state;
  }
};

export default userReducer;
