import { LOGOUT, SET_DASH_LOADING } from "./ActionTypes";
import JwtService from "../../JwtService/JwtService";
import { payloadTable, payloadBar, payloadPie } from "../payload";
import { toastr } from "react-redux-toastr";

export const getDashBoardData = (startDate, endDate) => {
  return (dispatch) => {
    let dateRange = {
      startDate,
      endDate,
    };
    dispatch({ type: SET_DASH_LOADING });
    let tablePayload = {
      ...payloadTable,
      chartObject: {
        ...payloadTable.chartObject,
        requestParam: {
          ...payloadTable.chartObject.requestParam,
          dateRange,
        },
      },
    };
    let barPayload = {
      ...payloadBar,
      chartObject: {
        ...payloadBar.chartObject,
        requestParam: {
          ...payloadBar.chartObject.requestParam,
          dateRange,
        },
      },
    };
    let piePayload = {
      ...payloadPie,
      chartObject: {
        ...payloadPie.chartObject,
        requestParam: {
          ...payloadPie.chartObject.requestParam,
          dateRange,
        },
      },
    };
    let table = JwtService.post("/getData", tablePayload);
    let bar = JwtService.post("/getData", barPayload);
    let pie = JwtService.post("/getData", piePayload);
    Promise.all([table, bar, pie])
      .then((response) => {
        //dispatch({ type: SET_DASHBOARD_DATA, payload: response });
      })
      .catch((error) => {
        if (error?.response?.status == 401) {
          dispatch({ type: LOGOUT });
          toastr.error("", "something went wrong");
        } else {
          console.log("error", error);
        }
      });
  };
};
