import { SET_USER, LOGIN_ERROR } from "../Actions/ActionTypes";

const initialState = {
  data: null,
  error: "",
};

const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_USER:
      return {
        ...state,
        data: action.data,
      };
    case LOGIN_ERROR:
      return {
        ...state,
        error: action.data,
      };
    default:
      return state;
  }
};

export default userReducer;
